                    <h1>Faça seu Logon</h1>

                    <input 
                        placeholder="Sua ID" 
                        value ={id}
                        onChan={ e => setId(e.target.value) } 
                    />
                    <button  className="button" type="submit">Entrar</button>

                    <Link className="back-link" to="/register">
                        <FiLogIn size={16} color="#E02041"/>
                        Não tenho cadastro 
                    </Link> 
                </form>

            </section>

            <img src={heroesImg} alt="Heroes" />
        </div>
    );
}
